// Extract order number from email
const emailSubject = $input.item.json.Subject || '';
const emailSnippet = $input.item.json.snippet || '';
const emailFrom = $input.item.json.From || '';

// Combine subject and snippet for searching
const fullText = `${emailSubject} ${emailSnippet}`;

console.log('Searching in text:', fullText);

// Regex patterns - ORDER MATTERS! Most specific first
const patterns = [
  /#(\d{3,10})/i,                           // #1234 (most common)
  /order\s*#\s*(\d{3,10})/i,                // order #1234, order# 1234
  /order\s+number\s*:?\s*#?\s*(\d{3,10})/i, // order number 1234, order number: 1234
  /order\s*:?\s*(\d{3,10})/i,               // order: 1234, order 1234
  /#([A-Z]{2}\d{3,8})/i,                    // #AB1234 (Shopify letter format)
  /order\s*#?\s*([A-Z]{2}\d{3,8})/i         // order AB1234
];

let orderNumber = null;

// Try each pattern
for (const pattern of patterns) {
  const match = fullText.match(pattern);
  if (match) {
    orderNumber = match[1];
    console.log('Pattern matched:', pattern, 'â†’ Order:', orderNumber);
    break;
  }
}

// If no order number found
if (!orderNumber) {
  return {
    json: {
      orderFound: false,
      originalEmail: $input.item.json,
      searchedText: fullText,
      errorMessage: "No order number found in email"
    }
  };
}

// Return the order number and original email data
return {
  json: {
    orderFound: true,
    orderNumber: orderNumber,
    customerEmail: emailFrom,
    emailSubject: emailSubject,
    emailBody: emailSnippet,
    originalEmail: $input.item.json
  }
};

// Check if order was found
const orders = $input.item.json.orders;

if (!orders || orders.length === 0) {
  return {
    json: {
      orderFound: false,
      orderNumber: $('Extract Order Number').item.json.orderNumber,
      errorMessage: "Order not found in Shopify"
    }
  };
}

// Get the first order (should only be one with exact name match)
const order = orders[0];

// Return structured order data
return {
  json: {
    orderFound: true,
    order: {
      id: order.id,
      name: order.name,
      email: order.email,
      created_at: order.created_at,
      total_price: order.total_price,
      currency: order.currency,
      financial_status: order.financial_status,
      fulfillment_status: order.fulfillment_status,
      line_items: order.line_items,
      customer: order.customer,
      shipping_address: order.shipping_address
    },
    customerEmail: $('Extract Order Number').item.json.customerEmail,
    requestedOrderNumber: $('Extract Order Number').item.json.orderNumber
  }
};

// Get order data from previous node
const orderData = $input.item.json.order;

// Return Policy Rules (you can customize these per merchant)
const policy = {
  returnWindowDays: 30,
  exchangeWindowDays: 30,
  autoApproveUnder: 50,
  requiresApprovalOver: 200,
  allowFinalSaleReturns: false,
  refundOriginalShipping: false
};

// Calculate if return is within time window
const orderDate = new Date(orderData.created_at);
const today = new Date();
const daysAgo = Math.floor((today - orderDate) / (1000 * 60 * 60 * 24));

const withinReturnWindow = daysAgo <= policy.returnWindowDays;

// Check order status
const isFulfilled = orderData.fulfillment_status === 'fulfilled' || orderData.fulfillment_status === 'partial';
const isPaid = orderData.financial_status === 'paid' || orderData.financial_status === 'partially_refunded';

// Check if any items are final sale (by tags)
const hasFinalSaleItems = orderData.line_items.some(item => {
  const productTitle = item.title.toLowerCase();
  const vendor = (item.vendor || '').toLowerCase();
  // Check if product has "final sale" or "clearance" in title
  return productTitle.includes('final sale') || 
         productTitle.includes('clearance') || 
         vendor.includes('clearance');
});

// Calculate total order value
const orderTotal = parseFloat(orderData.total_price);

// Determine if auto-approve or needs manual review
const autoApprove = orderTotal < policy.autoApproveUnder;
const needsApproval = orderTotal > policy.requiresApprovalOver;

// Determine overall eligibility
let eligible = true;
let ineligibleReasons = [];

if (!withinReturnWindow) {
  eligible = false;
  ineligibleReasons.push(`Return window expired (${daysAgo} days ago, limit is ${policy.returnWindowDays} days)`);
}

if (!isFulfilled) {
  eligible = false;
  ineligibleReasons.push('Order not yet fulfilled/shipped');
}

if (!isPaid) {
  eligible = false;
  ineligibleReasons.push('Order not paid');
}

if (hasFinalSaleItems && !policy.allowFinalSaleReturns) {
  eligible = false;
  ineligibleReasons.push('Order contains final sale items');
}

// Build detailed response
return {
  json: {
    eligible: eligible,
    order: {
      id: orderData.id,
      name: orderData.name,
      email: orderData.email,
      created_at: orderData.created_at,
      total_price: orderData.total_price,
      currency: orderData.currency,
      fulfillment_status: orderData.fulfillment_status,
      financial_status: orderData.financial_status,
      line_items: orderData.line_items,
      shipping_address: orderData.shipping_address
    },
    eligibility: {
      withinReturnWindow: withinReturnWindow,
      daysAgo: daysAgo,
      returnWindowDays: policy.returnWindowDays,
      isFulfilled: isFulfilled,
      isPaid: isPaid,
      hasFinalSaleItems: hasFinalSaleItems,
      autoApprove: autoApprove,
      needsApproval: needsApproval,
      orderTotal: orderTotal
    },
    ineligibleReasons: ineligibleReasons,
    policy: policy,
    customerEmail: $('Extract Order Number').item.json.customerEmail,
    requestedOrderNumber: $('Extract Order Number').item.json.orderNumber
  }
};
